package cmd

import (
	"fmt"
	"io/ioutil"
	"os"
	"path"

	"github.com/goodeggs/platform/cmd/ranch/util"
	"github.com/spf13/cobra"
)

const DOCKERFILE_TEMPLATE string = `
# generated by ranch
FROM goodeggs/cedar:latest
MAINTAINER Good Eggs <open-source@goodeggs.com>
# everything else handled by goodeggs/cedar ONBUILD commands
# see https://github.com/goodeggs/cedar
`

var deployCmd = &cobra.Command{
	Use:   "deploy",
	Short: "Deploy the application",
	Run: func(cmd *cobra.Command, args []string) {

		appName, err := util.AppName(cmd)

		util.Check(err)

		appDir, err := util.AppDir(cmd)

		util.Check(err)

		appVersion, err := util.AppVersion(cmd)

		util.Check(err)

		dockerfile := path.Join(appDir, "Dockerfile")

		if _, err := os.Stat(dockerfile); os.IsNotExist(err) {
			err := ioutil.WriteFile(dockerfile, []byte(DOCKERFILE_TEMPLATE), 0644)

			util.Check(err)

			defer os.Remove(dockerfile) // cleanup
		} else {
			fmt.Println("WARNING: using existing Dockerfile")
		}

		imageName, err := util.DockerBuild(appDir, appName, appVersion)

		util.Check(err)

		fmt.Println("built docker image", imageName)
	},
}

func init() {
	RootCmd.AddCommand(deployCmd)
}
